import React, { useState, useEffect, useContext } from 'react';


import cardsContext from "../../context/Cards/CardsContext";

import Card from '../Card/Card';
import * as Styled from './Deck.styles'
import { act } from 'react-dom/test-utils';

const Deck = () => {
  const CardsContext = useContext(cardsContext);
  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategory, setActiveCategory] = useState('');

  // useEffect(() => {
  //   CardsContext.getActiveCards()
  // }, [CardsContext])

  const handleCategoryClick = (e, categoryId, categorySlug) => {
    console.log(categorySlug);
    console.log(activeCategory)
    setActiveCategory(prevActiveCategory => {
      console.log('prev', prevActiveCategory);
      return categoryId
    });
  }

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const cardCount = Object.values(CardsContext.cards).map(c => c.questions).flat().length;

    if (increment > 0) {
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    }
  }

  useEffect(() => {
    CardsContext.getActiveCards(activeCategory);
  }, []);

  return (
    <Styled.Deck>
      <>
      <nav css={Styled.CategoriesNav}>
      {(CardsContext.activeCards && CardsContext.activeCards.length > 0)
        ? Object.values(CardsContext.activeCards).map((fullCategory, i) => (
          <a key={`${fullCategory._id}-${i}`}
             onClick={(e) => handleCategoryClick(e, fullCategory._id, fullCategory.slug)}>{fullCategory.name}</a>)) 
        : Object.values(CardsContext.activeCards).map((fullCategory, i) => (
          <a key={`${fullCategory._id}-${i}`}
             onClick={(e) => handleCategoryClick(e, fullCategory._id, fullCategory.slug)}>{fullCategory.name}</a>))}
        </nav>
      </>
    </Styled.Deck>
  );
};

export default Deck;
