import React, { useContext, useEffect, useState } from "react";
import Card from '../Card/Card';
import cardsCtxt, { ActiveCardsContext, CategoriesContext } from "../../context/Cards/CardsContext";
import * as Styled from './Deck.styles'

const Deck = () => {
  const cardsCtxt = useContext(CardsContext);
  const activeCardsContext = useContext(ActiveCardsContext);
  const categoriesContext = useContext(CategoriesContext);

  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategory, setActiveCategory] = useState(null);

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const cardCount = Object.values(cardsCtxt.cards).map(c => c.questions).flat().length;
    if (increment > 0) {
      // If advancing to next
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      // If going back to previous
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    } 
  }

  useEffect(() => {
    // cardsCtxt.getCategories();
    // console.log(cardsCtxt.categories);
    // cardsCtxt.getActiveCards('');
  }, []);

  return (
    <Styled.Deck>
      {cardsCtxt.cards
        ? Object.values(cardsCtxt.cards)
          .flat()
          .map(fullCategory => fullCategory.questions)
          .flat()
          .map((question, i) => {
            return (
              <Card 
                data={question}
                activeSide={`front`}
                key={`${i}-${question._id}`}
                position={i}
                isActive={i === activeIndex}
                navClickHandler={() => handleNavClick}
              />
            )
          })
        : null}
    </Styled.Deck>
  );
};

export default Deck;
