import React, { useState, useEffect, useContext } from 'react';
import cardsContext from "../../context/Cards/CardsContext";
import Card from '../Card/Card';
import * as Styled from './Deck.styles'

const Deck = () => {
  const CardsContext = useContext(cardsContext);
  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategoryId, setActiveCategoryId] = useState('');

  const handleCategoryClick = (e, categoryId, categorySlug) => {
    // Set active index back to 0 since we're switching categories
    setActiveIndex(0)
    setActiveCategoryId(activeCategoryId)
    CardsContext.getActiveCards(activeCategoryId)
  }

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const cardCount = Object.values(CardsContext.activeCards).map(c => c.questions).flat().length;

    if (increment > 0) {
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    }
  }

  useEffect(() => {
    CardsContext.getCategories();
    CardsContext.getActiveCards(activeCategoryId);
  }, []);

  return (
    <Styled.Deck>

      <Styled.AppHeader>
        <Styled.AppHeading>Front End Dev Flash Cards</Styled.AppHeading>
        <Styled.CategoriesNav>
          {console.log(CardsContext.categories.sort((a, b) => a < b))}
          {/* {
            (CardsContext.activeCards && CardsContext.activeCards.length > 0)
            ? Object.values(CardsContext.activeCards).map((fullCategory, i) => (
                <a key={`${fullCategory._id}-${i}`}
                  onClick={(e) => handleCategoryClick(e, fullCategory._id, fullCategory.slug)}>{fullCategory.name}
                </a>)
              )
            : Object.values(CardsContext.activeCards).map((fullCategory, i) => (
                <a key={`${fullCategory._id}-${i}`}
                  onClick={(e) => handleCategoryClick(e, fullCategory._id, fullCategory.slug)}>{fullCategory.name}
                </a>)
              )
          } */}
        </Styled.CategoriesNav>
      </Styled.AppHeader>

      { 
        (CardsContext.activeCards && CardsContext.activeCards.length > 0)
        ? Object.values(CardsContext.activeCards)
            .map(fullCategory => fullCategory.questions)
            .flat()
            .map((question, i) => (
              <Card 
                data={question}
                activeSide='front'
                key={`${i}-${question._id}`}
                position={i}
                isActive={activeIndex === i}
                navClickHandler={() => handleNavClick} /> ))
        : Object.values(CardsContext.activeCards)
            .map(fullCategory => fullCategory.questions)
            .flat()
            .map((question, i) => ( 
              <Card 
                data={question}
                activeSide='front'
                key={`${i}-${question._id}`}
                position={i}
                isActive={activeIndex === i}
                navClickHandler={() => handleNavClick} /> ))
        }
    </Styled.Deck>
  );
};

export default Deck;
