import React, { useReducer } from 'react';
import axios from 'axios';
import CardsContext from './CardsContext';
import CardsReducer from './CardsReducer';
import { GET_CARDS, GET_ACTIVE_CARDS, GET_CATEGORIES } from '../types';


const CardsState = (props) => {
  let initialState = {
    cards: [],
    activeCards: [],
    categories: [],
    currentCard: 0
  };

  const [ state, dispatch ] = useReducer(CardsReducer, initialState);

  const getCards = async () => {
    try {
      const res = await axios.get("https://arcane-stream-45843.herokuapp.com/populated-categories");
      const { data } = res
      dispatch({ type: GET_CARDS, payload: data });
    } catch (error) {
      console.error(error);
    }
  };

  const getActiveCards = async (category_id='') => {
    try {
      const res = (category_id === '') 
        ? await axios.get(`https://arcane-stream-45843.herokuapp.com/populated-categories`)
        : await axios.get(`https://arcane-stream-45843.herokuapp.com/categories/${category_id}/questions`)
      const { data } = res

      dispatch({ type: GET_ACTIVE_CARDS, payload: data });
    } catch (error) {
      console.error(error);
    }
  }

  const getCategories = async () => {
    try {
      const res = await axios.get(`https://arcane-stream-45843.herokuapp.com/categories`);
      const { data } = res
      dispatch({ type: GET_CATEGORIES, payload: data });
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <CardsContext.Provider
      value={{
        categories: state.categories,
        cards: state.cards,
        activeCards: state.activeCards,
        currentCard: state.currentCard,
        getCategories,
        getCards,
        getActiveCards,
      }}
    >
      {props.children}
    </CardsContext.Provider>
  );
}

export default CardsState;
