import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Register from './components/User/Register';
import Login from './components/User/Login';
import Profile from './components/User/Profile';
import Deck from './components/Deck/Deck';
import Theme from './Theme';
import CardsState from './context/Cards/CardsState';
import ThemeProvider from "./ThemeProvider"
import * as Styled from './App.styles'
import { Helmet, HelmetProvider } from 'react-helmet-async';

export const App = () => {

  const isLoggedIn = () => {
    return localStorage.getItem('TOKEN_KEY') != null;
  };

  return (
    <CardsState category_id={''}>
      <ThemeProvider theme={Theme}>
        <HelmetProvider>
          <Helmet><title>Front End Development Flash Cards</title></Helmet>
          <Styled.Layout>
            <div className='main-wrapper'>
              <Router>
                <ul>
                {isLoggedIn()
                  ? (<><li><Link to='/login'>Login</Link></li><li><Link to='/register'>Register</Link></li></>)
                  : (<><li><Link to='/profile'>Profile</Link></li><li><Link to='/logout'>Logout</Link></li></>)
                } 
                  <li><Link to='/'>Flash Cards</Link></li>
                </ul>
                <Switch>
                  <Route exact={true} path='/register'><Register /></Route>
                  <Route exact={true} path='/profile'><Profile _id="5f4eee85019783ff01e49e57"/></Route>
                  <Route exact={true} path='/login'><Login /></Route>
                  <Route exact={true} path='/'><Deck /></Route>
                </Switch>
              </Router>
            </div>
          </Styled.Layout>
        </HelmetProvider>
      </ThemeProvider>
    </CardsState>
  );
}

export default App;
