import React, { useContext, useEffect, useState } from "react";
import Card from '../Card/Card';
import CardsContext from "../../context/Cards/CardsContext";
import * as Styled from './Deck.styles'

const Deck = () => {
  const ContextCards = useContext(CardsContext);
  const [activeIndex, setActiveIndex] = useState(0);
  // const ContextActiveCards = useContext(ActiveCardsContext);
  // const ContextCategories = useContext(CategoriesContext);


  // const [activeCategory, setActiveCategory] = useState(null);

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const cardCount = Object.values(ContextCards.cards).map(c => c.questions).flat().length;
    if (increment > 0) {
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    }
  }

  useEffect(() => {
    ContextCards.getCards();
    ContextCards.getActiveCards();
    ContextCards.getCategories();
    console.log(ContextCards.activeCards);
    console.log(ContextCards.categories);
  }, []);

  return (
    <CardsState>
      <Styled.Deck>
        { ContextCards.activeCards.length
          ? console.log(ContextCards.activeCards)
          : console.log(ContextCards.cards) }

          {/* Object.values(ContextCards.cards)
          .flat()
          .map(fullCategory => fullCategory.questions)
          .flat()
          .map((question, i) => { 
          //     return (
          //       <Card 
          //         data={question}
          //         activeSide={`front`}
          //         key={`${i}-${question._id}`}
          //         position={i}
          //         isActive={i === activeIndex}
          //         navClickHandler={() => handleNavClick}
          //       />
          //     )
          //   })
          // : null}*/}
        </Styled.Deck>
      </CardsState>
  );
};

export default Deck;
