import React from 'react';
import ReactHtmlParser from 'react-html-parser'
import Gist from '../Gist/Gist'

const level = difficulty => {
  if (difficulty <= 3) {
    return {
      text: 'Easy',
      tagClass: 'fa-tachometer-slowest'
    }
  } else if (difficulty > 7) {
    return {
      tagClass: 'fa-tachometer-fastest', 
      text: 'Hard'
    } 
  }
  return {
    tagClass: 'fa-tachometer-average',
    text: 'Mid-Level'
  } 
}

const Card = ({...data}) => {
  const { type, difficulty, tags } = data;
  const levelData = level(difficulty)

  return (

    <section className={type}>
      <header className='rainbow-border'>
        <div className='subheader'>
          <div>
            <span className="level tag">
              <i className={`fas ${levelData.tagClass}`}></i>
              &nbsp;{levelData.text}
            </span>
          </div>
          <div><h3>
            {type === 'front' ? `Question` : `Answer`}
          </h3></div>
          <div>
            <span className={`category tag`}>
              <i className={`fad fa-tag`}></i>
              &nbsp;{tags[0]}
            </span>
          </div>
        </div>
      </header>

    </section>
  )
}

export default Card;
