import React, { useContext, useEffect, useState } from "react";
import Card from '../Card/Card';
import cardsContext from "../../context/Cards/CardsContext";
import * as Styled from './Deck.styles'

const Deck = () => {
  const CardsContext = useContext(cardsContext);
  // const CategoriesContext = useContext(categoriesContext);
  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategory, setActiveCategory] = useState('all');
  const [categoryCards, setCategoryCards] = useState(null);
  const obj = {};
  const categories = Object.values(CardsContext.cards).forEach(q => {
    obj[q.slug] = {name: q.name, question_ids: q.questions};
  });

  const handleCategoryClick = categorySlug => {
    setActiveCategory(categorySlug)
    setCategoryCards(activeCategory === 'all' ? CardsContext.cards : CardsContext.cards[activeCategory])
  }

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const cardCount = Object.values(CardsContext.cards).map(c => c.questions).flat().length;

    if (increment > 0) {
      // If advancing to next
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      // If going back to previous
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    } 
  }

  useEffect(() => {
    CardsContext.getCards();
    setCategoryCards(activeCategory === 'all' ? CardsContext.cards : CardsContext.cards[activeCategory])
  }, [categoryCards, activeCategory]);

  return (
    <Styled.Deck>
      <nav>
        <ul>
          <li key='all'>
            <a onClick={() => handleCategoryClick('all')}>Clear All</a>  
          </li>
          { categories
            ? Object.values(categoryCards).map(q => (
              <li key={q.slug}>
                <a onClick={() => handleCategoryClick(q.slug)}>{q.name}</a>
              </li>)) 
            : null}
        </ul>
      </nav>
      { CardsContext.cards
        ? Object.values(CardsContext.cards).flat()
          .map(fullCategory => fullCategory.questions).flat()
          .map((question, i) => ((
            <Card 
              data={question}
              activeSide={`front`}
              key={`${i}-${question._id}`}
              position={i}
              isActive={i === activeIndex}
              navClickHandler={() => handleNavClick} />
            )))
        : null }
    </Styled.Deck>
  );
};

export default Deck;
