export const level = difficulty => {
  if (difficulty <= 3) {
    return {
      text: 'Easy',
      tagClass: 'fa-tachometer-slowest'
    }
  } else if (difficulty > 7) {
    return {
      tagClass: 'fa-tachometer-fastest', 
      text: 'Hard'
    } 
  }
  return {
    tagClass: 'fa-tachometer-average',
    text: 'Mid-Level'
  } 
}
