import React, { useContext, useEffect, useState } from "react";
import Card from '../Card/Card';
import cardsContext from "../../context/Cards/CardsContext";
import * as Styled from './Deck.styles'

const Deck = () => {
  const CardsContext = useContext(cardsContext);
  const [activeIndex, setActiveIndex] = useState(0);
  const [activeCategory, setActiveCategory] = useState('all');
  const [categoryCards, setCategoryCards] = useState([])

  const handleCategoryClick = (e, categorySlug) => {
    e.preventDefault();
    console.log('categorySlug', categorySlug);
    // setCategoryCards(activeCategory === 'all' ? CardsContext.cards : CardsContext.cards[activeCategory])
    setActiveIndex(0);
    setActiveCategory(categorySlug)
    setCategoryCards(activeCategory === 'all' ? CardsContext.cards : CardsContext.cards[activeCategory])
  }

  const handleNavClick = e => {
    const increment = e.target.tagName === 'BUTTON' ? e.target.dataset.increment : e.target.parentNode.dataset.increment;
    const activeCards = activeCategory === 'all' ? CardsContext.cards : CardsContext.cards[activeCategory]
    const cardCount = Object.values(activeCards).map(c => c.questions).flat().length;

    if (increment > 0) {
      // If advancing to next
      setActiveIndex(prevActiveIndex => prevActiveIndex + 1 < cardCount ? prevActiveIndex + 1 : 0)
    } else { 
      // If going back to previous
      setActiveIndex(prevActiveIndex => prevActiveIndex - 1 >= 0 ? prevActiveIndex - 1 : cardCount - 1) 
    } 
  }

  useEffect(() => {
    CardsContext.getCards();
    console.log('activeCategory', activeCategory);
    console.log('categoryCards', categoryCards);
    console.log('activeIndex', activeIndex);

  }, [CardsContext]);

  return (
    <Styled.Deck>
      <nav><ul>
        <li key={'all'}><a onClick={e => handleCategoryClick(e, 'all')}>Clear All</a></li>
        { categoryCards.length > 0
          ? Object.values(categoryCards).map((fullCategory, i) => (
            <li key={`${i}-${fullCategory.slug}`}><a onClick={e => handleCategoryClick(e, fullCategory.slug) }>{fullCategory.name}</a></li>
          )) : null }
        </ul></nav>
      { categoryCards.length > 0
        ? Object.values(categoryCards).map(fullCategory => (
            fullCategory.questions.map((question, i) => (
              <Card 
                data={question}
                activeSide={`front`}
                key={`${i}-${question._id}`}
                position={i}
                isActive={i === activeIndex}
                navClickHandler={() => handleNavClick}  />
            ))
          ))
        : null }
    </Styled.Deck>
  );
};

export default Deck;
